        title: "Luxury Manor in West Omaha",
        description: "Exceptional luxury home with premium finishes and extensive amenities",
        price: "1995000",
        address: "13824 Cuming Street",
        city: "Omaha",
        state: "NE",
        zipCode: "68154",
        beds: 5,
        baths: "6.0",
        sqft: 7460,
        yearBuilt: 2018,
        propertyType: "Single Family",
