import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Code, 
  Eye, 
  EyeOff, 
  Plus, 
  Settings, 
  Users, 
  MessageSquare, 
  TrendingUp,
  Save,
  Trash2,
  Edit,
  Shield,
  Database
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { insertTrackingCodeSchema, type TrackingCode, type InsertTrackingCode, type Lead } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import IdxAdmin from "@/components/sections/idx-admin";

export default function Admin() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = useState("tracking");
  const [editingCode, setEditingCode] = useState<TrackingCode | null>(null);

  // Authentication check (simple implementation)
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState("");

  const form = useForm<InsertTrackingCode>({
    resolver: zodResolver(insertTrackingCodeSchema),
    defaultValues: {
      name: "",
      code: "",
      type: "pixel",
      active: true,
    },
  });

  // Simple password protection
  const handleLogin = () => {
    if (password === "admin123") { // In production, use proper authentication
      setIsAuthenticated(true);
      toast({
        title: "Access Granted",
        description: "Welcome to the admin panel.",
      });
    } else {
      toast({
        title: "Access Denied",
        description: "Invalid password.",
        variant: "destructive",
      });
    }
  };

  // Queries
  const { data: trackingCodes, isLoading: trackingLoading } = useQuery<TrackingCode[]>({
    queryKey: ["/api/tracking-codes"],
    enabled: isAuthenticated,
  });

  const { data: leads, isLoading: leadsLoading } = useQuery<Lead[]>({
    queryKey: ["/api/leads"],
    enabled: isAuthenticated,
  });

  // Mutations
  const createTrackingMutation = useMutation({
    mutationFn: async (data: InsertTrackingCode) => {
      return apiRequest("POST", "/api/tracking-codes", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tracking-codes"] });
      form.reset();
      toast({
        title: "Success",
        description: "Tracking code created successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create tracking code.",
        variant: "destructive",
      });
    },
  });

  const updateTrackingMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<InsertTrackingCode> }) => {
      return apiRequest("PUT", `/api/tracking-codes/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tracking-codes"] });
      setEditingCode(null);
      toast({
        title: "Success",
        description: "Tracking code updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update tracking code.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertTrackingCode) => {
    if (editingCode) {
      updateTrackingMutation.mutate({ id: editingCode.id, data });
    } else {
      createTrackingMutation.mutate(data);
    }
  };

  const toggleCodeStatus = (code: TrackingCode) => {
    updateTrackingMutation.mutate({
      id: code.id,
      data: { active: !code.active }
    });
  };

  const startEditing = (code: TrackingCode) => {
    setEditingCode(code);
    form.reset({
      name: code.name,
      code: code.code,
      type: code.type as "pixel" | "script" | "meta",
      active: code.active,
    });
  };

  const cancelEditing = () => {
    setEditingCode(null);
    form.reset();
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20 flex items-center justify-center">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="bg-bjork-beige rounded-full p-3 w-12 h-12 flex items-center justify-center mx-auto mb-4">
              <Shield className="w-6 h-6 text-white" />
            </div>
            <CardTitle className="text-2xl font-display text-bjork-black">
              Admin Access
            </CardTitle>
            <p className="text-gray-600">Enter password to access admin panel</p>
          </CardHeader>
          <CardContent className="space-y-4">
            <Input
              type="password"
              placeholder="Enter admin password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
              className="border-gray-200 focus:ring-bjork-blue"
            />
            <Button 
              onClick={handleLogin}
              className="w-full bg-bjork-black text-white hover:bg-bjork-blue transition-colors duration-300"
            >
              Access Admin Panel
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="mb-8">
          <h1 className="text-4xl font-display font-light text-bjork-black mb-4">
            Admin <span className="text-bjork-beige">Panel</span>
          </h1>
          <p className="text-lg text-gray-600">
            Manage tracking codes, view leads, and monitor website performance
          </p>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-8">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="tracking" className="flex items-center">
              <Code className="w-4 h-4 mr-2" />
              Tracking Codes
            </TabsTrigger>
            <TabsTrigger value="leads" className="flex items-center">
              <Users className="w-4 h-4 mr-2" />
              Leads
            </TabsTrigger>
            <TabsTrigger value="idx" className="flex items-center">
              <Database className="w-4 h-4 mr-2" />
              IDX Management
            </TabsTrigger>
            <TabsTrigger value="analytics" className="flex items-center">
              <TrendingUp className="w-4 h-4 mr-2" />
              Analytics
            </TabsTrigger>
          </TabsList>

          {/* Tracking Codes Tab */}
          <TabsContent value="tracking" className="space-y-8">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              {/* Create/Edit Form */}
              <div className="lg:col-span-1">
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center">
                      <Plus className="w-5 h-5 mr-2" />
                      {editingCode ? 'Edit Tracking Code' : 'Add Tracking Code'}
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <Form {...form}>
                      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                        <FormField
                          control={form.control}
                          name="name"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Name</FormLabel>
                              <FormControl>
                                <Input placeholder="e.g., Google Analytics" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="type"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Type</FormLabel>
                              <Select onValueChange={field.onChange} value={field.value}>
                                <FormControl>
                                  <SelectTrigger>
                                    <SelectValue placeholder="Select type" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="pixel">Pixel</SelectItem>
                                  <SelectItem value="script">Script</SelectItem>
                                  <SelectItem value="meta">Meta Tag</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="code"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Code</FormLabel>
                              <FormControl>
                                <Textarea
                                  placeholder="Paste your tracking code here..."
                                  rows={8}
                                  {...field}
                                  className="font-mono text-sm"
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="active"
                          render={({ field }) => (
                            <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3">
                              <div className="space-y-0.5">
                                <FormLabel className="text-base">Active</FormLabel>
                                <div className="text-sm text-gray-500">
                                  Enable this tracking code
                                </div>
                              </div>
                              <FormControl>
                                <Switch
                                  checked={field.value || false}
                                  onCheckedChange={field.onChange}
                                />
                              </FormControl>
                            </FormItem>
                          )}
                        />

                        <div className="flex gap-2">
                          <Button 
                            type="submit" 
                            className="flex-1 bg-bjork-black text-white hover:bg-bjork-blue"
                            disabled={createTrackingMutation.isPending || updateTrackingMutation.isPending}
                          >
                            <Save className="w-4 h-4 mr-2" />
                            {editingCode ? 'Update' : 'Create'}
                          </Button>
                          {editingCode && (
                            <Button type="button" variant="outline" onClick={cancelEditing}>
                              Cancel
                            </Button>
                          )}
                        </div>
                      </form>
                    </Form>
                  </CardContent>
                </Card>
              </div>

              {/* Tracking Codes List */}
              <div className="lg:col-span-2">
                <Card>
                  <CardHeader>
                    <CardTitle>Tracking Codes</CardTitle>
                  </CardHeader>
                  <CardContent>
                    {trackingLoading ? (
                      <div className="space-y-4">
                        {[1, 2, 3].map((i) => (
                          <div key={i} className="border rounded-lg p-4 space-y-2">
                            <Skeleton className="h-5 w-1/3" />
                            <Skeleton className="h-4 w-1/4" />
                            <Skeleton className="h-16 w-full" />
                          </div>
                        ))}
                      </div>
                    ) : trackingCodes && trackingCodes.length > 0 ? (
                      <div className="space-y-4">
                        {trackingCodes.map((code) => (
                          <div key={code.id} className="border rounded-lg p-4">
                            <div className="flex items-center justify-between mb-2">
                              <h3 className="font-medium text-bjork-black">{code.name}</h3>
                              <div className="flex items-center gap-2">
                                <Badge variant={code.active ? "default" : "secondary"}>
                                  {code.active ? 'Active' : 'Inactive'}
                                </Badge>
                                <Badge variant="outline">{code.type}</Badge>
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() => toggleCodeStatus(code)}
                                >
                                  {code.active ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                                </Button>
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() => startEditing(code)}
                                >
                                  <Edit className="w-4 h-4" />
                                </Button>
                              </div>
                            </div>
                            <div className="bg-gray-50 rounded p-3 font-mono text-sm text-gray-600 overflow-x-auto">
                              <pre className="whitespace-pre-wrap break-all">
                                {code.code.length > 200 ? code.code.substring(0, 200) + '...' : code.code}
                              </pre>
                            </div>
                            <div className="text-xs text-gray-500 mt-2">
                              Created {formatDistanceToNow(new Date(code.createdAt!), { addSuffix: true })}
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-8">
                        <Code className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                        <p className="text-gray-500">No tracking codes found</p>
                      </div>
                    )}
                  </CardContent>
                </Card>
              </div>
            </div>
          </TabsContent>

          {/* Leads Tab */}
          <TabsContent value="leads">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <MessageSquare className="w-5 h-5 mr-2" />
                  Contact Form Leads
                </CardTitle>
              </CardHeader>
              <CardContent>
                {leadsLoading ? (
                  <div className="space-y-4">
                    {[1, 2, 3, 4, 5].map((i) => (
                      <div key={i} className="border rounded-lg p-4 space-y-2">
                        <div className="flex gap-4">
                          <Skeleton className="h-5 w-1/4" />
                          <Skeleton className="h-5 w-1/4" />
                          <Skeleton className="h-5 w-1/4" />
                        </div>
                        <Skeleton className="h-16 w-full" />
                      </div>
                    ))}
                  </div>
                ) : leads && leads.length > 0 ? (
                  <div className="overflow-x-auto">
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>Name</TableHead>
                          <TableHead>Contact</TableHead>
                          <TableHead>Interest</TableHead>
                          <TableHead>Property</TableHead>
                          <TableHead>Message</TableHead>
                          <TableHead>Date</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {leads.map((lead) => (
                          <TableRow key={lead.id}>
                            <TableCell className="font-medium">
                              {lead.firstName} {lead.lastName}
                            </TableCell>
                            <TableCell>
                              <div className="space-y-1">
                                <div className="text-sm">{lead.email}</div>
                                {lead.phone && (
                                  <div className="text-sm text-gray-500">{lead.phone}</div>
                                )}
                              </div>
                            </TableCell>
                            <TableCell>
                              {lead.interest && (
                                <Badge variant="outline">
                                  {lead.interest.charAt(0).toUpperCase() + lead.interest.slice(1)}
                                </Badge>
                              )}
                            </TableCell>
                            <TableCell>
                              {lead.propertyAddress ? (
                                <div className="text-sm text-gray-600 max-w-32 truncate">
                                  {lead.propertyAddress}
                                </div>
                              ) : (
                                <span className="text-gray-400">N/A</span>
                              )}
                            </TableCell>
                            <TableCell>
                              <div className="text-sm max-w-48 truncate" title={lead.message || ""}>
                                {lead.message}
                              </div>
                            </TableCell>
                            <TableCell className="text-sm text-gray-500">
                              {formatDistanceToNow(new Date(lead.createdAt!), { addSuffix: true })}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <MessageSquare className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">No leads found</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* IDX Management Tab */}
          <TabsContent value="idx">
            <IdxAdmin />
          </TabsContent>

          {/* Analytics Tab */}
          <TabsContent value="analytics">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-600">Total Leads</p>
                      <p className="text-3xl font-bold text-bjork-black">
                        {leads?.length || 0}
                      </p>
                    </div>
                    <Users className="w-8 h-8 text-bjork-beige" />
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-600">Active Tracking Codes</p>
                      <p className="text-3xl font-bold text-bjork-black">
                        {trackingCodes?.filter(c => c.active).length || 0}
                      </p>
                    </div>
                    <Code className="w-8 h-8 text-bjork-beige" />
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-600">This Week</p>
                      <p className="text-3xl font-bold text-bjork-black">
                        {leads?.filter(lead => {
                          const leadDate = new Date(lead.createdAt!);
                          const weekAgo = new Date();
                          weekAgo.setDate(weekAgo.getDate() - 7);
                          return leadDate > weekAgo;
                        }).length || 0}
                      </p>
                    </div>
                    <TrendingUp className="w-8 h-8 text-bjork-beige" />
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-600">System Status</p>
                      <p className="text-3xl font-bold text-green-600">Online</p>
                    </div>
                    <Settings className="w-8 h-8 text-bjork-beige" />
                  </div>
                </CardContent>
              </Card>
            </div>

            <Card className="mt-8">
              <CardHeader>
                <CardTitle>Recent Activity</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {leads?.slice(0, 5).map((lead) => (
                    <div key={lead.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div>
                        <p className="font-medium text-bjork-black">
                          New lead from {lead.firstName} {lead.lastName}
                        </p>
                        <p className="text-sm text-gray-500">
                          Interested in {lead.interest || 'real estate services'}
                        </p>
                      </div>
                      <div className="text-sm text-gray-500">
                        {formatDistanceToNow(new Date(lead.createdAt!), { addSuffix: true })}
                      </div>
                    </div>
                  )) || (
                    <p className="text-gray-500 text-center py-4">No recent activity</p>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/blog.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Skeleton } from "@/components/ui/skeleton";
import { Search, Calendar, User, Clock, TrendingUp } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { BlogPost } from "@shared/schema";

export default function Blog() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("");

  const { data: posts, isLoading, error } = useQuery<BlogPost[]>({
    queryKey: ["/api/blog", { published: true }],
  });

  // Filter posts based on search and category
  const filteredPosts = posts?.filter((post) => {
    const matchesSearch = !searchQuery || 
      post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      post.excerpt?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      post.content.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesCategory = !selectedCategory || post.category === selectedCategory;

    return matchesSearch && matchesCategory;
  }) || [];

  // Get unique categories
  const categories = Array.from(new Set(posts?.map(post => post.category) || []));

  // Featured post (most recent)
  const featuredPost = posts?.[0];
  const recentPosts = filteredPosts.slice(1);

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
          <div className="text-center py-12">
            <p className="text-red-600 mb-4">Failed to load blog posts</p>
            <Button onClick={() => window.location.reload()} variant="outline">
              Retry
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Header */}
        <div className="text-center mb-16">
          <h1 className="text-4xl md:text-6xl font-display font-light text-bjork-black mb-6">
            Market <span className="text-bjork-beige">Insights</span>
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Stay informed with the latest Nebraska real estate trends, market analysis, and expert insights from the Bjork Group team.
          </p>
        </div>

        {/* Search and Filter */}
        <div className="flex flex-col md:flex-row gap-4 mb-12">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <Input
              type="text"
              placeholder="Search articles..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 border-gray-200 focus:ring-bjork-blue"
            />
          </div>
          <Select value={selectedCategory || "all"} onValueChange={(value) => setSelectedCategory(value === "all" ? "" : value)}>
            <SelectTrigger className="w-full md:w-48 border-gray-200 focus:ring-bjork-blue">
              <SelectValue placeholder="All Categories" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Categories</SelectItem>
              {categories.map((category) => (
                <SelectItem key={category} value={category}>
                  {category}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {(searchQuery || selectedCategory) && (
            <Button
              variant="outline"
              onClick={() => {
                setSearchQuery("");
                setSelectedCategory("");
              }}
              className="border-bjork-beige text-bjork-black hover:bg-bjork-beige hover:text-white"
            >
              Clear
            </Button>
          )}
        </div>

        {/* Results Count */}
        {!isLoading && (
          <div className="mb-8">
            <p className="text-gray-600">
              {filteredPosts.length} {filteredPosts.length === 1 ? 'article' : 'articles'} found
              {searchQuery && ` for "${searchQuery}"`}
              {selectedCategory && ` in ${selectedCategory}`}
            </p>
          </div>
        )}

        {isLoading ? (
          <div className="space-y-12">
            {/* Featured Post Skeleton */}
            <Card className="overflow-hidden">
              <div className="grid grid-cols-1 lg:grid-cols-2">
                <Skeleton className="h-80 w-full" />
                <div className="p-8 space-y-4">
                  <Skeleton className="h-6 w-24" />
                  <Skeleton className="h-8 w-full" />
                  <Skeleton className="h-20 w-full" />
                  <div className="flex gap-4">
                    <Skeleton className="h-4 w-24" />
                    <Skeleton className="h-4 w-20" />
                  </div>
                  <Skeleton className="h-10 w-32" />
                </div>
              </div>
            </Card>

            {/* Recent Posts Skeleton */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {[1, 2, 3, 4, 5, 6].map((i) => (
                <Card key={i} className="overflow-hidden">
                  <Skeleton className="h-48 w-full" />
                  <div className="p-6 space-y-4">
                    <Skeleton className="h-4 w-20" />
                    <Skeleton className="h-6 w-full" />
                    <Skeleton className="h-16 w-full" />
                    <div className="flex justify-between">
                      <Skeleton className="h-4 w-24" />
                      <Skeleton className="h-4 w-20" />
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        ) : filteredPosts.length > 0 ? (
          <div className="space-y-12">
            {/* Featured Post */}
            {!searchQuery && !selectedCategory && featuredPost && (
              <Card className="overflow-hidden hover:shadow-2xl transition-all duration-300 property-card">
                <div className="grid grid-cols-1 lg:grid-cols-2">
                  <div className="relative h-80 lg:h-auto">
                    <img 
                      src={featuredPost.image || 'https://images.unsplash.com/photo-1554224155-6726b3ff858f?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'}
                      alt={featuredPost.title}
                      className="w-full h-full object-cover"
                    />
                    <div className="absolute top-4 left-4">
                      <Badge className="bg-bjork-blue text-white flex items-center">
                        <TrendingUp className="w-3 h-3 mr-1" />
                        Featured
                      </Badge>
                    </div>
                  </div>
                  <div className="p-8 flex flex-col justify-between">
                    <div>
                      <Badge className="bg-bjork-beige text-white mb-4">
                        {featuredPost.category}
                      </Badge>
                      <h2 className="text-3xl font-display font-medium text-bjork-black mb-4 leading-tight">
                        {featuredPost.title}
                      </h2>
                      <p className="text-gray-600 mb-6 leading-relaxed line-clamp-3">
                        {featuredPost.excerpt}
                      </p>
                    </div>
                    <div>
                      <div className="flex items-center justify-between text-sm text-gray-500 mb-6">
                        <div className="flex items-center">
                          <User className="w-4 h-4 mr-1" />
                          <span>{featuredPost.author}</span>
                        </div>
                        <div className="flex items-center">
                          <Clock className="w-4 h-4 mr-1" />
                          <span>{formatDistanceToNow(new Date(featuredPost.createdAt!), { addSuffix: true })}</span>
                        </div>
                      </div>
                      <Link href={`/blog/${featuredPost.slug}`}>
                        <Button className="bg-bjork-black text-white hover:bg-bjork-blue transition-colors duration-300">
                          Read Full Article
                        </Button>
                      </Link>
                    </div>
                  </div>
                </div>
              </Card>
            )}

            {/* Recent Posts Grid */}
            <div>
              {recentPosts.length > 0 && (
                <h2 className="text-2xl font-display font-medium text-bjork-black mb-8">
                  {searchQuery || selectedCategory ? 'Search Results' : 'Recent Articles'}
                </h2>
              )}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {(searchQuery || selectedCategory ? filteredPosts : recentPosts).map((post) => (
                  <Link key={post.id} href={`/blog/${post.slug}`}>
                    <Card className="overflow-hidden hover:shadow-xl transition-shadow duration-300 cursor-pointer property-card">
                      <div className="relative h-48">
                        <img 
                          src={post.image || 'https://images.unsplash.com/photo-1554224155-6726b3ff858f?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'}
                          alt={post.title}
                          className="w-full h-full object-cover"
                        />
                        <div className="absolute top-4 left-4">
                          <Badge className="bg-bjork-beige text-white">
                            {post.category}
                          </Badge>
                        </div>
                      </div>
                      <CardContent className="p-6">
                        <h3 className="text-xl font-display font-medium text-bjork-black mb-3 line-clamp-2 leading-tight">
                          {post.title}
                        </h3>
                        <p className="text-gray-600 mb-4 line-clamp-3 leading-relaxed">
                          {post.excerpt}
                        </p>
                        <div className="flex items-center justify-between text-sm text-gray-500">
                          <div className="flex items-center">
                            <User className="w-3 h-3 mr-1" />
                            <span>{post.author}</span>
                          </div>
                          <div className="flex items-center">
                            <Calendar className="w-3 h-3 mr-1" />
                            <span>{formatDistanceToNow(new Date(post.createdAt!), { addSuffix: true })}</span>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  </Link>
                ))}
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center py-16">
            <div className="mb-6">
              <Search className="w-16 h-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-2xl font-display text-gray-600 mb-4">No Articles Found</h3>
              <p className="text-gray-500 mb-8">
                {searchQuery || selectedCategory 
                  ? "Try adjusting your search criteria to see more results."
                  : "We're currently working on new content. Check back soon!"
                }
              </p>
            </div>
            {(searchQuery || selectedCategory) && (
              <Button 
                onClick={() => {
                  setSearchQuery("");
                  setSelectedCategory("");
                }}
                className="bg-bjork-black text-white hover:bg-bjork-blue"
              >
                View All Articles
              </Button>
            )}
          </div>
        )}

        {/* Newsletter Signup */}
        <div className="mt-20 bg-bjork-blue rounded-lg p-12 text-center text-white">
          <h2 className="text-3xl font-display font-light mb-6">
            Stay Informed with <span className="text-bjork-beige">Market Updates</span>
          </h2>
          <p className="text-xl mb-8 opacity-90 max-w-2xl mx-auto">
            Get the latest Nebraska real estate insights, market trends, and exclusive property updates delivered to your inbox.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center max-w-md mx-auto">
            <Input
              type="email"
              placeholder="Enter your email address"
              className="bg-white/20 border-white/30 text-white placeholder-white/70 focus:ring-bjork-beige"
            />
            <Button className="bg-bjork-beige text-white hover:bg-white hover:text-bjork-blue transition-colors duration-300 whitespace-nowrap">
              Subscribe
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/contact.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { Phone, Mail, MapPin, Clock, MessageSquare, Calendar } from "lucide-react";
import { contactFormSchema, type ContactForm } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { CONTACT_INFO, SOCIAL_LINKS } from "@/lib/constants";
import { Facebook, Twitter, Linkedin, Instagram } from "lucide-react";

export default function Contact() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<ContactForm>({
    resolver: zodResolver(contactFormSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      propertyAddress: "",
      interest: undefined,
      message: "",
    },
  });

  const submitMutation = useMutation({
    mutationFn: async (data: ContactForm) => {
      return apiRequest("POST", "/api/contact", data);
    },
    onSuccess: () => {
      toast({
        title: "Message Sent Successfully!",
        description: "Thank you for contacting us. We'll get back to you within 24 hours.",
      });
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error Sending Message",
        description: error.message || "Please try again or call us directly.",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    },
  });

  const onSubmit = (data: ContactForm) => {
    setIsSubmitting(true);
    submitMutation.mutate(data);
  };

  const contactMethods = [
    {
      icon: Phone,
      title: "Phone",
      details: CONTACT_INFO.phone,
      subtext: "Call or text anytime",
      action: `tel:${CONTACT_INFO.phone.replace(/[^\d]/g, '')}`
    },
    {
      icon: Mail,
      title: "Email", 
      details: "mike.bjork@bhhsamb.com",
      subtext: "We'll respond within 24 hours",
      action: "mailto:mike.bjork@bhhsamb.com"
    },
    {
      icon: MapPin,
      title: "Office",
      details: CONTACT_INFO.address.street,
      subtext: `${CONTACT_INFO.address.city}, ${CONTACT_INFO.address.state} ${CONTACT_INFO.address.zip}`,
      action: `https://maps.google.com?q=${encodeURIComponent(`${CONTACT_INFO.address.street}, ${CONTACT_INFO.address.city}, ${CONTACT_INFO.address.state} ${CONTACT_INFO.address.zip}`)}`
    },
    {
      icon: Clock,
      title: "Hours",
      details: "Open 24 hours 7 days a week",
      subtext: "Available whenever you need us",
      action: null
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Header */}
        <div className="text-center mb-16">
          <h1 className="text-4xl md:text-6xl font-display font-light text-bjork-black mb-6">
            Contact <span className="text-bjork-beige">Us</span>
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Ready to start your Nebraska real estate journey? Get in touch with our luxury real estate specialists. We're here to make your home buying or selling experience exceptional.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-12">
          {/* Contact Information */}
          <div className="lg:col-span-1 space-y-8">
            <div>
              <h2 className="text-2xl font-display font-medium text-bjork-black mb-6">
                Get in Touch
              </h2>
              <div className="space-y-6">
                {contactMethods.map((method, index) => (
                  <div key={index} className="flex items-start">
                    <div className="bg-bjork-beige rounded-full p-3 mr-4 flex-shrink-0">
                      <method.icon className="w-5 h-5 text-white" />
                    </div>
                    <div className="flex-1">
                      <h3 className="font-medium text-bjork-black mb-1">{method.title}</h3>
                      {method.action ? (
                        <a 
                          href={method.action}
                          className="text-gray-600 hover:text-bjork-blue transition-colors duration-300 block"
                          target={method.action.startsWith('http') ? '_blank' : undefined}
                          rel={method.action.startsWith('http') ? 'noopener noreferrer' : undefined}
                        >
                          {method.details}
                        </a>
                      ) : (
                        <div className="text-gray-600">{method.details}</div>
                      )}
                      <div className="text-sm text-gray-500 mt-1">{method.subtext}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Social Media */}
            <div>
              <h3 className="text-lg font-display font-medium text-bjork-black mb-4">Follow Us</h3>
              <div className="flex space-x-4">
                <a 
                  href={SOCIAL_LINKS.facebook} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-bjork-beige text-white p-3 rounded-full hover:bg-bjork-blue transition-colors duration-300"
                >
                  <Facebook className="w-5 h-5" />
                </a>
                <a 
                  href={SOCIAL_LINKS.twitter} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-bjork-beige text-white p-3 rounded-full hover:bg-bjork-blue transition-colors duration-300"
                >
                  <Twitter className="w-5 h-5" />
                </a>
                <a 
                  href={SOCIAL_LINKS.linkedin} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-bjork-beige text-white p-3 rounded-full hover:bg-bjork-blue transition-colors duration-300"
                >
                  <Linkedin className="w-5 h-5" />
                </a>
                <a 
                  href={SOCIAL_LINKS.instagram} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="bg-bjork-beige text-white p-3 rounded-full hover:bg-bjork-blue transition-colors duration-300"
                >
