                })}
                <Link href="/contact" onClick={() => setIsOpen(false)}>
                  <Button className="w-full bg-bjork-black text-white hover:bg-bjork-blue transition-colors duration-300">
                    Contact Us
                  </Button>
                </Link>
              </div>
            </SheetContent>
          </Sheet>
        </div>
      </div>
    </nav>
  );
}

