import { useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { X } from "lucide-react";

interface VideoPlayerProps {
  videoId: string;
  onClose: () => void;
  autoplay?: boolean;
}

export default function VideoPlayer({ videoId, onClose, autoplay = true }: VideoPlayerProps) {
  const iframeRef = useRef<HTMLIFrameElement>(null);

  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape") {
        onClose();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [onClose]);

  const embedUrl = `https://www.youtube.com/embed/${videoId}?${autoplay ? 'autoplay=1&' : ''}rel=0&modestbranding=1&showinfo=0`;

  return (
    <div className="relative w-full h-full bg-black rounded-lg overflow-hidden">
      <Button
        onClick={onClose}
        className="absolute top-4 right-4 z-10 bg-black/50 hover:bg-black/70 text-white rounded-full p-2"
        size="icon"
      >
        <X className="w-4 h-4" />
      </Button>

      <iframe
        ref={iframeRef}
        src={embedUrl}
        title="YouTube video player"
        className="w-full h-full"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
        allowFullScreen
      />
    </div>
  );
}

client/src/pages/template-admin.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Upload, Save, Eye, Globe, Settings, Palette, User, FileText } from "lucide-react";
import { templateSchema, type Template, type InsertTemplate } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function TemplateAdmin() {
  const [isUploading, setIsUploading] = useState(false);

  // Query current template
  const { data: template, isLoading } = useQuery({
    queryKey: ['/api/template'],
    enabled: true
  });

  const form = useForm<InsertTemplate>({
    resolver: zodResolver(templateSchema),
    defaultValues: template || {
      companyName: "Your Real Estate Company",
      agentName: "Your Name",
      agentTitle: "Principal Broker",
      agentEmail: "",
      phone: "",
      companyDescription: "We believe that luxury is not a price point but an experience.",
      agentBio: "Professional real estate agent with years of experience.",
      homesSold: 500,
      totalSalesVolume: "$200M+",
      yearsExperience: 15,
      clientSatisfaction: "98%",
      serviceAreas: ["Your Primary City", "Your Secondary City"],
      primaryColor: "hsl(20, 14.3%, 4.1%)",
      accentColor: "hsl(213, 100%, 45%)",
      beigeColor: "hsl(25, 35%, 75%)",
      subdomain: "",
      customDomain: "",
      isActive: true
    },
  });

  const updateTemplateMutation = useMutation({
    mutationFn: async (data: InsertTemplate) => {
      return apiRequest("POST", "/api/template", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/template'] });
    },
  });

  const onSubmit = (data: InsertTemplate) => {
    updateTemplateMutation.mutate(data);
  };

  const handleFileUpload = async (file: File, category: string) => {
    setIsUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('category', category);

      const result = await apiRequest("POST", "/api/template/upload", formData) as { fileUrl: string };
      // Update form with new file URL
      if (category === 'logo') {
        form.setValue('logoUrl', result.fileUrl);
      } else if (category === 'hero') {
        form.setValue('heroImageUrl', result.fileUrl);
      } else if (category === 'agent') {
        form.setValue('agentImageUrl', result.fileUrl);
      }
    } catch (error) {
      console.error('Upload failed:', error);
    } finally {
      setIsUploading(false);
    }
  };

  if (isLoading) {
    return <div className="pt-20 p-8">Loading template...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Template Customization</h1>
          <p className="text-gray-600 mt-2">
            Customize your real estate website template with your branding, content, and media.
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Preview Card */}
          <div className="lg:col-span-1">
            <Card className="sticky top-24">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Eye className="w-5 h-5" />
                  Live Preview
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="text-sm">
                  <strong>Company:</strong> {form.watch('companyName')}
                </div>
                <div className="text-sm">
                  <strong>Agent:</strong> {form.watch('agentName')}
                </div>
                <div className="text-sm">
                  <strong>Homes Sold:</strong> {form.watch('homesSold')}+
                </div>
                <div className="text-sm">
                  <strong>Sales Volume:</strong> {form.watch('totalSalesVolume')}
                </div>
                <div className="space-y-2">
                  <div className="text-sm font-medium">Domain Options:</div>
                  {form.watch('subdomain') && (
                    <Badge variant="secondary">
                      {form.watch('subdomain')}.realestatesite.com
                    </Badge>
                  )}
                  {form.watch('customDomain') && (
                    <Badge variant="default">
                      {form.watch('customDomain')}
                    </Badge>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Main Form */}
          <div className="lg:col-span-3">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)}>
                <Tabs defaultValue="company" className="space-y-6">
                  <TabsList className="grid w-full grid-cols-5">
                    <TabsTrigger value="company" className="flex items-center gap-2">
                      <User className="w-4 h-4" />
                      Company
                    </TabsTrigger>
                    <TabsTrigger value="content" className="flex items-center gap-2">
                      <FileText className="w-4 h-4" />
                      Content
                    </TabsTrigger>
                    <TabsTrigger value="media" className="flex items-center gap-2">
                      <Upload className="w-4 h-4" />
                      Media
                    </TabsTrigger>
                    <TabsTrigger value="branding" className="flex items-center gap-2">
                      <Palette className="w-4 h-4" />
                      Branding
                    </TabsTrigger>
                    <TabsTrigger value="domains" className="flex items-center gap-2">
                      <Globe className="w-4 h-4" />
                      Domains
                    </TabsTrigger>
                  </TabsList>

                  {/* Company Information */}
                  <TabsContent value="company">
                    <Card>
                      <CardHeader>
                        <CardTitle>Company Information</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <FormField
                            control={form.control}
                            name="companyName"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Company Name</FormLabel>
                                <FormControl>
                                  <Input placeholder="Your Real Estate Company" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="agentName"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Agent Name</FormLabel>
                                <FormControl>
                                  <Input placeholder="Your Name" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <FormField
                            control={form.control}
                            name="agentTitle"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Agent Title</FormLabel>
                                <FormControl>
                                  <Input placeholder="Principal Broker" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="agentEmail"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Email Address</FormLabel>
                                <FormControl>
                                  <Input type="email" placeholder="agent@yourcompany.com" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>

                        <FormField
                          control={form.control}
                          name="phone"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Phone Number</FormLabel>
                              <FormControl>
                                <Input placeholder="(555) 123-4567" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </CardContent>
                    </Card>
                  </TabsContent>

                  {/* Content */}
                  <TabsContent value="content">
                    <Card>
                      <CardHeader>
                        <CardTitle>Content & Statistics</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <FormField
                          control={form.control}
                          name="companyDescription"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Company Description</FormLabel>
                              <FormControl>
                                <Textarea 
                                  rows={4}
                                  placeholder="We believe that luxury is not a price point but an experience..." 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="agentBio"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Agent Biography</FormLabel>
                              <FormControl>
                                <Textarea 
                                  rows={6}
                                  placeholder="Professional real estate agent with years of experience..." 
                                  {...field} 
                                />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                          <FormField
                            control={form.control}
                            name="homesSold"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Homes Sold</FormLabel>
                                <FormControl>
                                  <Input 
                                    type="number" 
                                    placeholder="500"
                                    {...field}
                                    onChange={e => field.onChange(parseInt(e.target.value))}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="totalSalesVolume"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Sales Volume</FormLabel>
                                <FormControl>
                                  <Input placeholder="$200M+" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="yearsExperience"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Years Experience</FormLabel>
                                <FormControl>
                                  <Input 
                                    type="number" 
                                    placeholder="15"
                                    {...field}
                                    onChange={e => field.onChange(parseInt(e.target.value))}
                                  />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="clientSatisfaction"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Client Satisfaction</FormLabel>
                                <FormControl>
                                  <Input placeholder="98%" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>
                      </CardContent>
                    </Card>
                  </TabsContent>

                  {/* Media Upload */}
                  <TabsContent value="media">
                    <Card>
                      <CardHeader>
                        <CardTitle>Media Files</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                          <div className="space-y-2">
                            <label className="text-sm font-medium">Company Logo</label>
                            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                              <Upload className="w-8 h-8 mx-auto text-gray-400 mb-2" />
                              <p className="text-sm text-gray-600">Upload Logo</p>
                              <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => e.target.files?.[0] && handleFileUpload(e.target.files[0], 'logo')}
                                className="mt-2"
                              />
                            </div>
                          </div>

                          <div className="space-y-2">
                            <label className="text-sm font-medium">Hero Image</label>
                            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                              <Upload className="w-8 h-8 mx-auto text-gray-400 mb-2" />
                              <p className="text-sm text-gray-600">Upload Hero Image</p>
                              <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => e.target.files?.[0] && handleFileUpload(e.target.files[0], 'hero')}
                                className="mt-2"
                              />
                            </div>
                          </div>

                          <div className="space-y-2">
                            <label className="text-sm font-medium">Agent Photo</label>
                            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                              <Upload className="w-8 h-8 mx-auto text-gray-400 mb-2" />
                              <p className="text-sm text-gray-600">Upload Agent Photo</p>
                              <input
                                type="file"
                                accept="image/*"
                                onChange={(e) => e.target.files?.[0] && handleFileUpload(e.target.files[0], 'agent')}
                                className="mt-2"
                              />
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  </TabsContent>

                  {/* Branding */}
                  <TabsContent value="branding">
                    <Card>
                      <CardHeader>
                        <CardTitle>Brand Colors</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                          <FormField
                            control={form.control}
                            name="primaryColor"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Primary Color</FormLabel>
                                <FormControl>
                                  <Input placeholder="hsl(20, 14.3%, 4.1%)" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="accentColor"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Accent Color</FormLabel>
                                <FormControl>
                                  <Input placeholder="hsl(213, 100%, 45%)" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                          <FormField
                            control={form.control}
                            name="beigeColor"
                            render={({ field }) => (
                              <FormItem>
                                <FormLabel>Secondary Color</FormLabel>
                                <FormControl>
                                  <Input placeholder="hsl(25, 35%, 75%)" {...field} />
                                </FormControl>
                                <FormMessage />
                              </FormItem>
                            )}
                          />
                        </div>
                      </CardContent>
                    </Card>
                  </TabsContent>

                  {/* Domains */}
                  <TabsContent value="domains">
                    <Card>
                      <CardHeader>
                        <CardTitle>Domain Configuration</CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-6">
                        <FormField
                          control={form.control}
                          name="subdomain"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Subdomain</FormLabel>
                              <FormControl>
                                <div className="flex items-center">
                                  <Input placeholder="youragent" {...field} />
                                  <span className="ml-2 text-gray-500">.realestatesite.com</span>
                                </div>
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={form.control}
                          name="customDomain"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Custom Domain (Optional)</FormLabel>
                              <FormControl>
                                <Input placeholder="www.youragency.com" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </CardContent>
                    </Card>
                  </TabsContent>
                </Tabs>

                <div className="flex justify-end space-x-4 mt-8">
                  <Button type="submit" disabled={updateTemplateMutation.isPending || isUploading}>
                    <Save className="w-4 h-4 mr-2" />
                    {updateTemplateMutation.isPending ? "Saving..." : "Save Template"}
                  </Button>
                </div>
              </form>
            </Form>
          </div>
        </div>
      </div>
    </div>
  );
}

server/external-api.ts.content:
interface CMAPipelineProperty {
  id?: string;
  address?: string;
  city?: string;
  state?: string;
  zip_code?: string;
  price?: number;
  beds?: number;
  baths?: number;
  sqft?: number;
  year_built?: number;
  property_type?: string;
  status?: string;
  listing_date?: string;
  days_on_market?: number;
  lat?: number;
  lng?: number;
  description?: string;
  photos?: string[];
  mls_id?: string;
  listing_agent?: string;
  listing_office?: string;
}

export class ExternalPropertyAPI {
  private baseUrl = 'http://gbcma.us-east-2.elasticbeanstalk.com/api';

  async getPropertiesForCity(city: string, options: {
    minPrice?: number;
    maxPrice?: number;
    limit?: number;
  } = {}): Promise<CMAPipelineProperty[]> {
    try {
      const params = new URLSearchParams({
        city,
        ...(options.minPrice && { min_price: options.minPrice.toString() }),
        ...(options.maxPrice && { max_price: options.maxPrice.toString() }),
      });

      const response = await fetch(`${this.baseUrl}/cma-comparables?${params}`, {
        headers: {
          'Accept': '*/*',
          'User-Agent': 'BjorkHomes/1.0',
        },
      });

      if (!response.ok) {
        console.error(`CMA API error: ${response.status} ${response.statusText}`);
        return [];
      }

      const data = await response.json();

      // Handle both array and single object responses
      const properties = Array.isArray(data) ? data : (data.properties || []);

      // Sort by price descending and limit results
      const sortedProperties = properties
        .filter((p: any) => p.price && p.price > 0)
        .sort((a: any, b: any) => (b.price || 0) - (a.price || 0));

      return options.limit ? sortedProperties.slice(0, options.limit) : sortedProperties;
    } catch (error) {
      console.error('Error fetching properties from CMA API:', error);
      return [];
    }
  }

  async getLuxuryProperties(): Promise<CMAPipelineProperty[]> {
    try {
      // Get high-end properties from Omaha and Lincoln
      const [omahaProperties, lincolnProperties] = await Promise.all([
        this.getPropertiesForCity('Omaha', { minPrice: 500000, maxPrice: 2000000, limit: 10 }),
        this.getPropertiesForCity('Lincoln', { minPrice: 400000, maxPrice: 1500000, limit: 10 })
      ]);

      // Combine and sort by price descending
      const allProperties = [...omahaProperties, ...lincolnProperties]
        .sort((a, b) => (b.price || 0) - (a.price || 0));

      return allProperties.slice(0, 6); // Return top 6 luxury properties
    } catch (error) {
      console.error('Error fetching luxury properties:', error);
      return [];
    }
  }

  // Transform CMA API data to our property schema
  transformToProperty(cmaProperty: CMAPipelineProperty): any {
    return {
      mlsId: cmaProperty.mls_id || cmaProperty.id || `CMA_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      title: this.generatePropertyTitle(cmaProperty),
      description: cmaProperty.description || this.generateDescription(cmaProperty),
      price: cmaProperty.price?.toString() || "0",
      address: cmaProperty.address || "",
      city: cmaProperty.city || "",
      state: cmaProperty.state || "NE",
      zipCode: cmaProperty.zip_code || "",
      beds: cmaProperty.beds || 3,
      baths: cmaProperty.baths?.toString() || "2.0",
      sqft: cmaProperty.sqft || 2000,
      yearBuilt: cmaProperty.year_built || 2020,
      propertyType: cmaProperty.property_type || "Single Family",
      status: "active",
      featured: true,
      luxury: (cmaProperty.price || 0) > 400000,
      images: this.generatePropertyImages(cmaProperty),
      neighborhood: this.extractNeighborhood(cmaProperty),
      schoolDistrict: this.getSchoolDistrict(cmaProperty.city),
      style: this.getPropertyStyle(cmaProperty),
      coordinates: cmaProperty.lat && cmaProperty.lng ? {
        lat: cmaProperty.lat,
        lng: cmaProperty.lng
      } : this.getDefaultCoordinates(cmaProperty.city),
      features: this.generateFeatures(cmaProperty),
      architecturalStyle: this.determineArchitecturalStyle(cmaProperty),
      secondaryStyle: null,
      styleConfidence: "0.75",
      styleFeatures: this.getStyleFeatures(cmaProperty),
      styleAnalyzed: true,
      isIdxListing: false,
      createdAt: new Date(),
      updatedAt: new Date()
    };
  }

  private generatePropertyTitle(property: CMAPipelineProperty): string {
    const price = property.price ? `$${(property.price / 1000).toFixed(0)}K` : "";
    const city = property.city || "Nebraska";
    const beds = property.beds || 3;
    const baths = property.baths || 2;

    return `${beds}BR/${baths}BA Luxury Home in ${city} ${price}`.trim();
  }

  private generateDescription(property: CMAPipelineProperty): string {
    const city = property.city || "Nebraska";
    const sqft = property.sqft ? `${property.sqft.toLocaleString()} sq ft` : "spacious";
    const year = property.year_built ? `built in ${property.year_built}` : "modern construction";

    return `Beautiful luxury home in ${city} featuring ${sqft} of ${year} living space with premium finishes and modern amenities.`;
  }

  private generatePropertyImages(property: CMAPipelineProperty): string[] {
    // Use property photos if available, otherwise use high-quality stock images
    if (property.photos && property.photos.length > 0) {
      return property.photos;
    }

    // Return luxury home stock images
    return [
      "https://images.unsplash.com/photo-1600607687939-ce8a6c25118c?ixlib=rb-4.0.3&auto=format&fit=crop&w=2053&q=80",
      "https://images.unsplash.com/photo-1613490493576-7fde63acd811?ixlib=rb-4.0.3&auto=format&fit=crop&w=2071&q=80",
      "https://images.unsplash.com/photo-1600596542815-ffad4c1539a9?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80"
    ];
  }

  private extractNeighborhood(property: CMAPipelineProperty): string {
    // Extract neighborhood from address or use city
    if (property.address) {
      const parts = property.address.split(',');
      if (parts.length > 1) {
        return parts[parts.length - 2].trim();
      }
    }
    return property.city ? `${property.city} Area` : "Downtown";
  }

  private getSchoolDistrict(city?: string): string {
    const districts: Record<string, string> = {
      'Omaha': 'Omaha Public Schools',
      'Lincoln': 'Lincoln Public Schools',
      'Elkhorn': 'Elkhorn Public Schools',
      'Bellevue': 'Bellevue Public Schools',
      'Papillion': 'Papillion La Vista Schools'
    };
    return districts[city || ''] || 'Local School District';
  }

  private getPropertyStyle(property: CMAPipelineProperty): string {
    if (property.year_built && property.year_built > 2010) return "2 Story";
    if (property.sqft && property.sqft > 3000) return "2 Story";
    return "Ranch";
  }

  private getDefaultCoordinates(city?: string): { lat: number; lng: number } {
    const coordinates: Record<string, { lat: number; lng: number }> = {
      'Omaha': { lat: 41.2565, lng: -95.9345 },
      'Lincoln': { lat: 40.8136, lng: -96.7026 },
      'Elkhorn': { lat: 41.2871, lng: -96.2394 },
      'Bellevue': { lat: 41.1370, lng: -95.9145 },
      'Papillion': { lat: 41.1544, lng: -96.0422 }
    };
    return coordinates[city || ''] || coordinates['Omaha'];
  }

  private generateFeatures(property: CMAPipelineProperty): string[] {
    const features = ["Luxury Finishes"];

    if (property.sqft && property.sqft > 2500) {
      features.push("Spacious Layout");
    }
    if (property.year_built && property.year_built > 2015) {
      features.push("Modern Amenities");
    }
    if ((property.price || 0) > 600000) {
      features.push("Premium Location", "High-End Features");
    }

    return features;
  }

  private determineArchitecturalStyle(property: CMAPipelineProperty): string {
    if (property.year_built && property.year_built > 2015) return "Modern";
    if (property.year_built && property.year_built > 2000) return "Contemporary";
    return "Traditional";
  }

  private getStyleFeatures(property: CMAPipelineProperty): string[] {
    const year = property.year_built || 2020;
    if (year > 2015) {
      return ["Open concept", "Large windows", "Clean lines", "Modern finishes"];
    }
    return ["Classic design", "Quality craftsmanship", "Timeless appeal"];
  }
}

export const externalPropertyAPI = new ExternalPropertyAPI();

